def contatore(lista):
    segni = []
    for i in range(1,len(lista),1):
        if lista[i]!=lista[i-1]:
            segno = (lista[i]-lista[i-1])/abs(lista[i]-lista[i-1])
            segni.append(segno)
    #print(segni)
    
    contatore = 0


    for i in range(0,len(segni)-1,1):
        if segni[i] != segni[i+1]:
            contatore += 1
    
    return contatore

