def suddivisione(lista_dati):

    i = 0
    ore = []

    while i < len(lista_dati)-1:
        if i>0:
            insieme = [lista_dati[i-1][1],lista_dati[i][1]]
        if i == 0:
            insieme = [lista_dati[i][1]]
    
        
        start = i

        while lista_dati[i+1][0]//3600 == lista_dati[i][0]//3600 :
            insieme.append(lista_dati[i+1][1])
            i+=1
        
        
        
        if len(insieme) == 2:
            insieme = [lista_dati[start-1][1],lista_dati[start][1],lista_dati[start + 1][1]]
        
        

        ore.append(insieme)
        #print(i)
        i+=1

    return ore

def compito(lista_dati):

    insieme = suddivisione(lista_dati)
    result = []
    for elem in insieme:
        result.append(contatore(elem))
    
    return result

